    private void btnEnviarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnEnviarActionPerformed
      {
    String remitente = txtRemitente.getText();
String nombreRemitente = txtNombreRemitente.getText();
String receptor = txtReceptor.getText();
String nombreReceptor = txtNombreReceptor.getText();
String contenido = txtContenido.getText();
 
String mensaje = "De: " + nombreRemitente + " <" + remitente + ">\n"
        + "Para: " + nombreReceptor + " <" + receptor + ">\n"
        + "Mensaje:\n" + contenido;

AlmacenMensajes.mensajes.add(mensaje);


// Mostrar mensaje de confirmación
javax.swing.JOptionPane.showMessageDialog(this, " Mensaje enviado con éxito.", "Envío exitoso", javax.swing.JOptionPane.INFORMATION_MESSAGE);


}

    }//GEN-LAST:event_btnEnviarActionPerformed

