    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
 
    String correoIngresado = txtCorreo.getText().trim();

    if (correoIngresado.isEmpty()) {
        txtResultados.setText("Por favor, ingrese un correo.");
        return;
    }

    StringBuilder resultados = new StringBuilder();
    for (String mensaje : chatgui.AlmacenMensajes.mensajes) {
        if (mensaje.contains(correoIngresado)) {

            // Marcar como leído si aún no lo está
            if (!chatgui.AlmacenMensajes.mensajesLeidos.contains(mensaje)) {
                chatgui.AlmacenMensajes.mensajesLeidos.add(mensaje);
            }

            resultados.append(mensaje).append("\n\n");
        }
    }

    if (resultados.length() == 0) {
        txtResultados.setText("No se encontraron mensajes para el correo: " + correoIngresado);
    } else {
        txtResultados.setText(resultados.toString());
    }


    }//GEN-LAST:event_jButton1ActionPerformed


