    private void btnConsultarEstadoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnConsultarEstadoActionPerformed

    String correoIngresado = txtCorreoEstado.getText().trim();

    if (correoIngresado.isEmpty()) {
        txtResultadoEstado.setText("Por favor, ingrese un correo.");
        return;
    }

    StringBuilder resultados = new StringBuilder();
    for (String mensaje : chatgui.AlmacenMensajes.mensajes) {
        if (mensaje.contains(correoIngresado)) {
            boolean leido = chatgui.AlmacenMensajes.mensajesLeidos.contains(mensaje);

            resultados.append(mensaje)
                      .append("\nEstado: ")
                      .append(leido ? "Leído" : " No leído")
                      .append("\n\n");
        }
    }

    if (resultados.length() == 0) {
        txtResultadoEstado.setText("No se encontraron mensajes para el correo: " + correoIngresado);
    } else {
        txtResultadoEstado.setText(resultados.toString());
    }




    }//GEN-LAST:event_btnConsultarEstadoActionPerformed


